        background-color: var(--hover-background) !important;
        color: var(--main-color) !important;
        border-color: var(--hover-background) !important;
    }
    .desktop-notifications .browser .yt-uix-button.turn-on,
    .desktop-notifications .browser .yt-uix-button.turn-off,
    .resume-setting-button,
    .undo-setting-button-section,
    .resume-all-settings-button/*turn off*/
    {
        background: var(--main-background) !important;
        border-color: var(--main-background) !important;
        color: var(--main-color) !important;
    }
    .yt-uix-form-input-select/*dropdowns*/
    {
        background: var(--hover-background) !important;
        border-color: var(--hover-background) !important;
        color: var(--main-text) !important;
    }
    /*checkboxes*/
    tp-yt-paper-toggle-button[checked]:not([disabled]) .toggle-bar.tp-yt-paper-toggle-button {
        background-color: var(--main-color) !important;
        opacity: 1 !important;
    }
    tp-yt-paper-toggle-button[checked]:not([disabled]) .toggle-button.tp-yt-paper-toggle-button {
        background-color: var(--main-text) !important;
    }
    /*language*/
    ytd-dropdown-renderer[has-background] tp-yt-paper-dropdown-menu-light.ytd-dropdown-renderer {
        background-color: var(--hover-background) !important;
    }
    label.label-is-floating.tp-yt-paper-dropdown-menu-light,
    #label.ytd-dropdown-item-renderer {
        color: var(--main-text) !important;
    }
    #input.tp-yt-paper-dropdown-menu-light {
        color: var(--dimmer-text) !important;
    }
    /*Playback*/
    .yt-help-icon {
        filter: invert(1);
    }
    .yt-uix-clickcard-card-content,
    .yt-uix-hovercard-card-content {
        background: var(--hover-background) !important;
        border-color: var(--hover-background) !important;
        color: var(--main-text) !important;
    }
    .yt-uix-clickcard-card-border,
    .yt-uix-hovercard-card-border {
        border-color: var(--hover-background) !important;
    }
    .yt-uix-clickcard-card-flip .yt-uix-card-body-arrow-horizontal,
    .yt-uix-hovercard-card-flip .yt-uix-card-body-arrow-horizontal {
        border-right-color: var(--hover-background) !important;
    }
    .exp-kevlar-settings .account-page .yt-uix-button/*save buttons*/
    {
        background: var(--main-color) !important;
        border-color: var(--main-color) !important;
        color: var(--main-text) !important;
        opacity: .9;
    }
    .exp-kevlar-settings .account-page .yt-uix-button:hover {
        opacity: 1 !important;
    }
    /*Social*/
    .social-connector {
        background: var(--hover-background) !important;
        border-color: var(--hover-background) !important;
        color: var(--main-text) !important;
    }
    /*TV*/
    .howto-circle {
        background-color: var(--hover-background) !important;
        color: var(--main-color) !important;
    }
    .howto-text {
        color: var(--dimmer-text) !important;
    }
    .remote-icon {
        filter: invert(1) brightness(200%);
    }
    h3.pairing-section-header {
        color: var(--main-text)
    }
    .yt-uix-form-input-select,
    .yt-uix-form-input-text,
    .yt-uix-form-input-textarea {
        border-color: var(--hover-background) !important;
    }
    #account-page-header-avatar.howto-promo-image {
        filter: invert(1) grayscale(1);
    }
    /*TV code*/
    .yt-uix-form-input-select,
    .yt-uix-form-input-text,
    .yt-uix-form-input-textarea {
        background: var(--hover-background) !important;
        border-color: var(--hover-background) !important;
        color: var(--main-text) !important;
    }
    /*Footer*/
    body #footer-container {
        background: var(--hover-background) !important;
        border-color: var(--hover-background) !important;
        color: var(--main-text) !important;
    }
    #footer-main/*divider*/
    {
        border-color: var(--hover-background) !important;
    }
    /*links*/
    #footer-links-primary a {
        color: var(--main-text) !important;
    }
    #footer-links-secondary a {
        color: var(--dimmer-text) !important;
    }
    #footer-links-primary a:hover,
    #footer-links-secondary a:hover {
        color: var(--main-color) !important;
    }
    /*buttons*/
    #footer .yt-uix-button-group .yt-uix-button,
    #footer .pickers li,
    #footer .footer-history,
    #footer #google-help {
        background: var(--second-background) !important;
        border-color: var(--second-background) !important;
        color: var(--main-text) !important;
        box-shadow: none !important;
    }
    .yt-uix-button-default.yt-uix-button-toggled:hover {
        box-shadow: none !important;
    }
    #footer .yt-uix-button-icon-footer-language,
    #footer .yt-uix-button-icon-footer-history,
    #footer .yt-uix-button-icon-questionmark {
        filter: invert(1);
    }
    .yt-uix-button-arrow {
        border-top-color: var(--main-text) !important;
    }
    #yt-picker-country-footer,
    #yt-picker-language-footer,
    #yt-picker-safetymode-footer {
        background: var(--second-background) !important;
        border-color: var(--second-background) !important;
        color: var(--main-text) !important;
    }
    .yt-picker-header,
    #safety-form p.safety-submit/*divider*/
    {
        border-color: var(--hover-background) !important;
    }
    .yt-picker-header h3.yt,
    .yt-picker-content strong {
        color: var(--main-text) !important;
    }
    #yt-picker-country-footer .yt-default .yt-notes,
    p.yt-notes,
    #safety-mode-description li {
        color: var(--dimmer-text) !important;
    }
    #yt-picker-country-footer .yt-picker-content a,
    .yt-picker-content button {
        color: var(--main-color) !important;
    }
    #yt-picker-safetymode-footer button/*save*/
    {
        background: var(--main-color) !important;
        border-color: var(--main-color) !important;
        color: var(--main-text) !important;
        opacity: .9;
    }

    /*Notification buttom corner - for settings, watch later etc*/
    tp-yt-paper-toast {
        background: var(--hover-background) !important;
        color: var(--main-text) !important;
        box-shadow: var(--shadow) !important;
    }

    /*Topic auto-generated by youtube*/
    #auto-generated.ytd-interactive-tabbed-header-renderer,
    #metadata.ytd-interactive-tabbed-header-renderer {
        color: var(--dimmer-text) !important;
    }

    /*Keyboard shortcuts Shift-/ */
    ytd-hotkey-dialog-renderer[dialog][dialog][dialog] {
        background-color: var(--main-background) !important;
    }
    ytd-hotkey-dialog-section-option-renderer {
        border-color: var(--hover-background) !important;
    }


    /*SponsorBlock*/
    /*logo change*/
    #sponsorBlockPopupLogo,
    .sponsorSkipLogo {
        display: block !important;
        -moz-box-sizing: border-box !important;
        box-sizing: border-box !important;
        background: url(https://raw.githubusercontent.com/RaitaroH/YouTube-DeepDark/master/YT_Images/IconSponsorBlocker256px.png) top left no-repeat !important;
        background-size: 100% !important;
        position: relative !important;
    }
    #sponsorBlockPopupLogo {
        width: 40px !important;
        height: 40px !important;
        padding-left: 40px !important;
    }
    /*for overlays*/
    .sponsorSkipLogo {
        width: 18px !important;
        height: 18px !important;
        padding-left: 18px !important;
    }
    .sponsorBlockTooltip {
        background-color: var(--hover-background) !important;
    }

    /*Fix like, dislike and share icons and text using wrong color*/
    .yt-spec-button-shape-next__icon, .ytd-segmented-like-dislike-button-renderer > yt-button-shape > button > .yt-spec-button-shape-next--button-text-content, .ytd-menu-renderer > yt-button-shape > button > .yt-spec-button-shape-next--button-text-content, .yt-spec-button-shape-next > span#text{
        color: var(--main-text)
    }
    /*Player tooltip background*/
    .ytp-tooltip-text{
        background: var(--hover-background) !important
    }
}

@-moz-document url-prefix('https://www.youtube.com/redirect') {
    /*Are you sure you want to leave YouTube? redirect*/
    /*seems like youtube will never update this page so, yeah*/
    #invalid-token-redirect-warning-text/*h1*/
    {
        color: var(--main-text) !important;
    }
    /*span*/
    #redirect-main-text {
        color: var(--dimmer-text) !important;
    }
    #redirect-main-text .bolded {
        color: var(--main-color);
    }
    /*search*/
    #masthead-search-terms-border {
        border-color: var(--hover-background) !important;
        background-color: var(--hover-background) !important;
        box-shadow: var(--shadow) !important;
    }
    #masthead-search-terms {
        color: var(--main-text);
    }
    .search-button {
        background: var(--hover-background) !important;
        border-color: var(--hover-background) !important;
    }
    /*🔍 is missing, nice*/
    .search-button-content {
        background: no-repeat url("data:image/svg+xml;base64,") !important;
        filter: invert(1);
    }
    #redirect-backto-safety-button:hover {
        color: var(--main-text) !important;
    }
    /*logo icon is also missing, but this __should__ change in the future*/
    .logo {
        background: no-repeat url("https://raw.githubusercontent.com/RaitaroH/YouTube-DeepDark/master/YT_Images/Logo.png") !important;
        background-size: 70% !important;
        padding: 10px;
        width: 120px !important;
        height: 33px !important;
        box-sizing: border-box !important;
        position: relative;
        right: -30px;
    }
    #logo-container .content-region {
        color: var(--main-text) !important;
    }
}

/* ==UserStyle==
@name           Github Catppuccin
@namespace      github.com/catppuccin/github
@version        1.1.7
@description    Soothing pastel theme for GitHub
@author         Catppuccin
@updateURL      https://github.com/catppuccin/github/raw/main/catppuccin.user.css
@preprocessor   stylus
@var select theme "Theme" ["Latte", "Frappe", "Macchiato", "Mocha*"]
@var checkbox topbar "Topbar colors" 1
@var checkbox blueaccent "Blue Accent" 0
@var checkbox usefont "Use Inter font" 0
==/UserStyle== */
@-moz-document regexp("https:\/\/(gist\.)*github\.com(?!\/(marketplace|organizations\/plan)).*") {
  if (theme=="Latte") {
      $type = light

      $rosewater = #dc8a78
      $flamingo = #dd7878
      $pink = #ea76cb
      $mauve = #8839ef
      $red = #d20f39
      $maroon = #e64553
      $peach = #fe640b
      $yellow = #df8e1d
      $green = #40a02b
      $teal = #179299
      $sky = #04a5e5
      $sapphire = #209fb5
      $blue = #1e66f5
      $lavender = #7287fd
      $text = #4c4f69
      $subtext1 = #5c5f77
      $subtext0 = #6c6f85
      $overlay2 = #7c7f93
      $overlay1 = #8c8fa1
      $overlay0 = #9ca0b0
      $surface2 = #acb0be
      $surface1 = #bcc0cc
      $surface0 = #ccd0da
      $base = #eff1f5
      $mantle = #e6e9ef
      $crust = #dce0e8
      $shadow = #dbdfef
  }
  else if (theme=="Frappe") {
      $type = dark

      $rosewater = #f2d5cf
      $flamingo = #eebebe
      $pink = #f4b8e4
      $mauve = #ca9ee6
      $red = #e78284
      $maroon = #ea999c
      $peach = #ef9f76
      $yellow = #e5c890
      $green = #a6d189
      $teal = #81c8be
      $sky = #99d1db
      $sapphire = #85c1dc
      $blue = #8caaee
      $lavender = #babbf1
      $text = #c6d0f5
      $subtext1 = #b5bfe2
      $subtext0 = #a5adce
      $overlay2 = #949cbb
      $overlay1 = #838ba7
      $overlay0 = #737994
      $surface2 = #626880
      $surface1 = #51576d
      $surface0 = #414559
      $base = #303446
      $mantle = #292c3c
      $crust = #232634
      $shadow = #010409
  }
  else if (theme=="Macchiato") {
      $type = dark

      $rosewater = #f4dbd6
      $flamingo = #f0c6c6
      $pink = #f5bde6
      $mauve = #c6a0f6
      $red = #ed8796
      $maroon = #ee99a0
      $peach = #f5a97f
      $yellow = #eed49f
      $green = #a6da95
      $teal = #8bd5ca
      $sky = #91d7e3
      $sapphire = #7dc4e4
      $blue = #8aadf4
      $lavender = #b7bdf8
      $text = #cad3f5
      $subtext1 = #b8c0e0
      $subtext0 = #a5adcb
      $overlay2 = #939ab7
      $overlay1 = #8087a2
      $overlay0 = #6e738d
      $surface2 = #5b6078
      $surface1 = #494d64
      $surface0 = #363a4f
      $base = #24273a
      $mantle = #1e2030
      $crust = #181926
      $shadow = #010409
  }
  else if (theme=="Mocha") {
      $type = dark

      $rosewater = #f5e0dc
      $flamingo = #f2cdcd
      $pink = #f5c2e7
      $mauve = #cba6f7
      $red = #f38ba8
      $maroon = #eba0ac
      $peach = #fab387
      $yellow = #f9e2af
      $green = #a6e3a1
      $teal = #94e2d5
      $sky = #89dceb
      $sapphire = #74c7ec
      $lavender = #b4befe
      $blue = #89b4fa
      $text = #cdd6f4
      $subtext1 = #bac2de
      $subtext0 = #a6adc8
      $overlay2 = #9399b2
      $overlay1 = #7f849c
      $overlay0 = #6c7086
      $surface2 = #585b70
      $surface1 = #45475a
      $surface0 = #313244
      $base = #1e1e2e
      $mantle = #181825
      $crust = #11111b
      $shadow = #010409
  }

  /* Functions */
  hoverColor(col) {
      return blend(alpha(col, 0.7), $text)
  }

  activeColor(col) {
      return blend(alpha(col, 0.5), $text)
  }

  disabledColor(col) {
      return blend(alpha(col, 0.4), $base)
  }

  [data-color-mode="light"][data-light-theme*="dark"],
  [data-color-mode="light"][data-light-theme*="light"],
  [data-color-mode="dark"][data-dark-theme*="dark"],
  [data-color-mode="dark"][data-dark-theme*="light"],
  [data-color-mode="auto"][data-dark-theme*="dark"],
  [data-color-mode="auto"][data-dark-theme*="light"] {
      color-scheme: $type;

      --color-canvas-default-transparent: rgba(13, 17, 23, 0);
      --color-page-header-bg:  $base;
      --color-marketing-icon-primary: $sky;
      --color-marketing-icon-secondary: $blue;
      --color-diff-blob-addition-num-text: $text;
      --color-diff-blob-addition-fg: $text;
      --color-diff-blob-addition-num-bg: alpha($green, 0.3);
      --color-diff-blob-addition-line-bg: alpha($green, 0.15);
      --color-diff-blob-addition-word-bg: alpha($green, 0.3);
      --color-diff-blob-deletion-num-text: $text;
      --color-diff-blob-deletion-fg: $text;
      --color-diff-blob-deletion-num-bg: alpha($red, 0.3);
      --color-diff-blob-deletion-line-bg: alpha($red, 0.15);
      --color-diff-blob-deletion-word-bg: alpha($red, 0.3);
      --color-diff-blob-hunk-num-bg: alpha($blue, 0.4);
      --color-diff-blob-expander-icon: #8b949e;
      --color-diff-blob-selected-line-highlight-mix-blend-mode: screen;
      --color-diffstat-deletion-border: rgba(240, 246, 252, 0.1);
      --color-diffstat-addition-border: rgba(240, 246, 252, 0.1);
      --color-diffstat-addition-bg: $green;
      --color-search-keyword-hl: rgba(210, 153, 34, 0.4);
      --color-prettylights-syntax-comment: $overlay1;
      --color-prettylights-syntax-constant: $blue;
      --color-prettylights-syntax-entity: $blue;
      --color-prettylights-syntax-storage-modifier-import: $peach;
      --color-prettylights-syntax-entity-tag: $teal;
      --color-prettylights-syntax-keyword: $pink;
      --color-prettylights-syntax-string: $green;
      --color-prettylights-syntax-variable: $peach;
      --color-prettylights-syntax-brackethighlighter-unmatched: #f0f;
      --color-prettylights-syntax-invalid-illegal-text: $red;
      --color-prettylights-syntax-invalid-illegal-bg: alpha($red, 0.15);
      --color-prettylights-syntax-carriage-return-text: #f0f;
      --color-prettylights-syntax-carriage-return-bg: #f0f;
      --color-prettylights-syntax-string-regexp: #f0f;
      --color-prettylights-syntax-markup-list: #f0f;
      --color-prettylights-syntax-markup-heading: $teal;
      --color-prettylights-syntax-markup-italic: $yellow;
      --color-prettylights-syntax-markup-bold: $yellow;
      --color-prettylights-syntax-markup-deleted-text: $text;
      --color-prettylights-syntax-markup-deleted-bg: alpha($red, 0.4);
      --color-prettylights-syntax-markup-inserted-text: $text;
      --color-prettylights-syntax-markup-inserted-bg: alpha($green, 0.4);
      --color-prettylights-syntax-markup-changed-text: $text;
      --color-prettylights-syntax-markup-changed-bg: alpha($yellow, 0.4);
      --color-prettylights-syntax-markup-ignored-text: $text;
      --color-prettylights-syntax-markup-ignored-bg: #f0f;
      --color-prettylights-syntax-meta-diff-range: $mauve;
      --color-prettylights-syntax-brackethighlighter-angle: #f0f;
      --color-prettylights-syntax-sublimelinter-gutter-mark: #f0f;
      --color-prettylights-syntax-constant-other-reference-link: #f0f;
      --color-codemirror-text: $text;
      --color-codemirror-bg: $base;
      --color-codemirror-gutters-bg: $base;
      --color-codemirror-guttermarker-text: $base;
      --color-codemirror-guttermarker-subtle-text: #6e7681;
      --color-codemirror-linenumber-text: #8b949e;
      --color-codemirror-cursor: #c9d1d9;
      --color-codemirror-selection-bg: rgba(56, 139, 253, 0.4);
      --color-codemirror-activeline-bg: rgba(110, 118, 129, 0.1);
      --color-codemirror-matchingbracket-text: #c9d1d9;
      --color-codemirror-lines-bg: $base;
      --color-codemirror-syntax-comment: #8b949e;
      --color-codemirror-syntax-constant: #79c0ff;
      --color-codemirror-syntax-entity: #d2a8ff;
      --color-codemirror-syntax-keyword: #ff7b72;
      --color-codemirror-syntax-storage: #ff7b72;
      --color-codemirror-syntax-string: #a5d6ff;
      --color-codemirror-syntax-support: #79c0ff;
      --color-codemirror-syntax-variable: #ffa657;
      --color-checks-bg: $mantle;
      --color-checks-run-border-width: 1px;
      --color-checks-container-border-width: 1px;
      --color-checks-text-primary: #c9d1d9;
      --color-checks-text-secondary: #8b949e;
      --color-checks-text-link: #58a6ff;
      --color-checks-btn-icon: #8b949e;
      --color-checks-btn-hover-icon: #c9d1d9;
      --color-checks-btn-hover-bg: rgba(110, 118, 129, 0.1);
      --color-checks-input-text: #8b949e;
      --color-checks-input-placeholder-text: #6e7681;
      --color-checks-input-focus-text: #c9d1d9;
      --color-checks-input-bg: #161b22;
      --color-checks-input-shadow: 0 0 0 1px;
      --color-checks-donut-error: $red;
      --color-checks-donut-pending: $peach;
      --color-checks-donut-success: $green;
      --color-checks-donut-neutral: $overlay2;
      --color-checks-dropdown-text: #c9d1d9;
      --color-checks-dropdown-bg: #161b22;
      --color-checks-dropdown-border: #30363d;
      --color-checks-dropdown-shadow: rgba(1, 4, 9, 0.3);
      --color-checks-dropdown-hover-text: #c9d1d9;
      --color-checks-dropdown-hover-bg: rgba(110, 118, 129, 0.1);
      --color-checks-dropdown-btn-hover-text: #c9d1d9;
      --color-checks-dropdown-btn-hover-bg: rgba(110, 118, 129, 0.1);
      --color-checks-scrollbar-thumb-bg: rgba(110, 118, 129, 0.4);
      --color-checks-header-label-text: #8b949e;
      --color-checks-header-label-open-text: #c9d1d9;
      --color-checks-header-border: #21262d;
      --color-checks-header-icon: #8b949e;
      --color-checks-line-text: #8b949e;
      --color-checks-line-num-text: #6e7681;
      --color-checks-line-timestamp-text: #6e7681;
      --color-checks-line-hover-bg: rgba(110, 118, 129, 0.1);
      --color-checks-line-selected-bg: rgba(56, 139, 253, 0.15);
      --color-checks-line-selected-num-text: #58a6ff;
      --color-checks-line-dt-fm-text: #ffffff;
      --color-checks-line-dt-fm-bg: #9e6a03;
      --color-checks-gate-bg: rgba(187, 128, 9, 0.15);
      --color-checks-gate-text: #8b949e;
      --color-checks-gate-waiting-text: #d29922;
      --color-checks-step-header-open-bg: $surface0;
      --color-checks-step-error-text: $maroon;
      --color-checks-step-warning-text: $peach;
      --color-checks-logline-text: #8b949e;
      --color-checks-logline-num-text: #6e7681;
      --color-checks-logline-debug-text: $mauve;
      --color-checks-logline-error-text: #8b949e;
      --color-checks-logline-error-num-text: #6e7681;
      --color-checks-logline-error-bg: alpha($maroon, 0.15);
      --color-checks-logline-warning-text: $subtext1;
      --color-checks-logline-warning-num-text: $peach;
      --color-checks-logline-warning-bg: alpha($peach, 0.15);
      --color-checks-logline-command-text: $blue;
      --color-checks-logline-section-text: $green;
      if ($type == "dark") {
          --color-checks-ansi-black: $surface1;
          --color-checks-ansi-black-bright: $surface2;
          --color-checks-ansi-white: $subtext1;
          --color-checks-ansi-white-bright: $subtext0;
      } else {
          --color-checks-ansi-black: $subtext1;
          --color-checks-ansi-black-bright: $subtext0;
          --color-checks-ansi-white: $surface2;
          --color-checks-ansi-white-bright: $surface1;
