  /* Buttons */
  .btn-danger {
      background: var(--color-btn-danger-bg)
  }

  /* Issue tags */
  .hx_IssueLabel {
      --text: $text;
  }
  if ($type == light) { /* Maybe we could make a custom algorithm that converts the colors to be more catppuccin like? */
      @css {
          .hx_IssueLabel {
              --lightness-threshold: 0.453;
              --border-threshold: 0.96;
              --border-alpha: max(0, min(calc((var(--perceived-lightness) - var(--border-threshold)) * 100), 1));
              //color: hsl(0deg, 0%, calc(var(--lightness-switch) * 100%)) !important;
              color: var(--text) !important;
              background: rgb(var(--label-r), var(--label-g), var(--label-b)) !important;
              border-color: hsla(var(--label-h), calc(var(--label-s) * 1%), calc((var(--label-l) - 25) * 1%), var(--border-alpha)) !important;
          }
      }
  } else {
      @css {
          .hx_IssueLabel {
              --lightness-threshold: 0.6;
              --background-alpha: 0.18;
              --border-alpha: 0.3;
              --lighten-by: calc(((var(--lightness-threshold) - var(--perceived-lightness)) * 100) * var(--lightness-switch));
              color: hsl(var(--label-h), calc(var(--label-s) * 1%), calc((var(--label-l) + var(--lighten-by)) * 1%)) !important;
              background: rgba(var(--label-r), var(--label-g), var(--label-b), var(--background-alpha)) !important;
              border-color: hsla(var(--label-h), calc(var(--label-s) * 1%), calc((var(--label-l) + var(--lighten-by)) * 1%), var(--border-alpha)) !important;
          }
      }
  }

  /* Labels */
  .Label {
      color:  var(--color-accent-fg);
      background-color: $surface0;
      border-color: transparent;
  }

  .Label--secondary {
      background-color:  $surface0;
      color:  $text;
  }

  .Label--success {
      color: $base;
      background-color:  $green;
  }

  .Label--danger {
    background-color: var(--color-danger-emphasis);
    color: var(--color-btn-danger-text);
  }

  .timeline-comment-header .Label,
  .timeline-comment .Label {
      color: var(--color-fg-muted);
  }

  /* Search */
  .header-search-input {
      color:  $text !important;
  }
  .header-search-input::placeholder {
      color: $subtext1 !important;
  }

  /* Tags */
  .topic-tag {
      --color-accent-fg:  $blue;
      --color-accent-emphasis:  $blue;
      --color-accent-muted: alpha($blue, 0.4);
      --color-accent-subtle: alpha($blue, 0.15);
  }

  /* Misc */
  .col-md-8 {
      background-color:  $base;
  }

  /* Large notification */
  .flash {
      background:  $mantle;
      border-color: transparent;
  }

  /* Button Fix */
  .color-bg-subtle {
      background-color: transparent !important;
  }
  .command-palette-details-dialog .color-bg-subtle {
    background-color: $surface0 !important;
  }
  /* Button selection Fix */
  .navigation-item[aria-selected="true"] .color-fg-muted {
      color: $base !important;
  }
  /* Headers */
  h1, h2, h3, h4, h5, h6 {
      color: $subtext1
  }

  if (usefont) {
      body,
      .markdown-body {
          font-family: inter, sans-serif;
      }
  }

  ::selection {
      background: $surface2 !important;
  }

  /* New change visibility box star and watcher preview buttons Fix */
  .btn.color-fg-danger.cursor-default {
      color: var(--color-btn-danger-text) !important;
      background-color: var(--color-btn-danger-bg);
  }
  .btn.color-fg-danger.cursor-default:hover {
      background-color: var(--color-btn-danger-hover-bg);
  }
  .btn.color-fg-danger.cursor-default > svg.octicon {
      color: var(--color-btn-danger-text) !important;
  }

  /* New change visibility box danger buttons Fix */
  :where(#repo-visibility-proceed-button-public, #repo-visibility-proceed-button-private):not([data-next-stage]) {
      color: var(--color-btn-danger-text);
      background-color: var(--color-btn-danger-bg);
  }

  /* 2FA authentication box header Fix */
  body.session-authentication div.Box-header {
      background-color: inherit;
  }
}

