/* ==UserStyle==
@name           AniList Catppuccin
@namespace      github.com/catppuccin/anilist
@version        2.0.7
@description    Soothing pastel theme for AniList
@author         Catppuccin
@updateURL      https://github.com/catppuccin/anilist/raw/main/src/AnilistCatppuccin.user.css
@preprocessor   stylus
@var select theme "Theme" ["Latte", "Frappe", "Macchiato", "Mocha*", "Mocha-Old"]
@var select accent "Accent" ["Rosewater*", "Flamingo", "Pink", "Mauve", "Red", "Maroon", "Peach", "Yellow", "Green", "Teal", "Blue", "Sapphire", "Sky", "Lavender", "Gray"]
@var select contrast-color "Contrast Color" ["Rosewater", "Flamingo", "Pink", "Mauve", "Red*", "Maroon", "Peach", "Yellow", "Green", "Teal", "Blue", "Sapphire", "Sky", "Lavender", "Gray"]
==/UserStyle== */
@-moz-document domain("anilist.co"), domain("anichart.net") {

	if (theme=="Latte") {

		$type = light

        $rosewater =     222, 149, 132
        $flamingo =      221, 120, 120
        $pink =          236, 131, 208
        $mauve =         136, 57, 239
        $red =           210, 15, 57
        $maroon =        230, 69, 83
        $peach =         254, 100, 11
        $yellow =        228, 147, 32
        $green =         64, 160, 43
        $teal =          23, 146, 153
        $blue =          42, 110, 245
        $sapphire =      32, 159, 181
        $sky =           4, 165, 229
        $lavender =      114, 135, 253
        $text =          76, 79, 105
        $subtext1 =      92, 95, 119
        $subtext0 =      108, 111, 133
        $overlay2 =      124, 127, 147
        $overlay1 =      140, 143, 161
        $overlay0 =      156, 160, 176
        $surface2 =      172, 176, 190
        $surface1 =      188, 192, 204
        $surface0 =      204, 208, 218
        $base =          239, 241, 245
        $mantle =        230, 233, 239
        $crust =         220, 224, 232

	}

	else if (theme=="Frappe") {

		$type = dark

        $rosewater =     242, 213, 207
        $flamingo =      238, 190, 190
        $pink =          244, 184, 228
        $mauve =         202, 158, 230
        $red =           231, 130, 132
        $maroon =        234, 153, 156
        $peach =         239, 159, 118
        $yellow =        229, 200, 144
        $green =         166, 209, 137
        $teal =          129, 200, 190
        $blue =          140, 170, 238
        $sapphire =      133, 193, 220
        $sky =           153, 209, 219
        $lavender =      186, 187, 241
        $text =          198, 206, 239
        $subtext1 =      181, 189, 220
        $subtext0 =      165, 172, 201
        $overlay2 =      148, 155, 183
        $overlay1 =      131, 138, 164
        $overlay0 =      115, 120, 145
        $surface2 =      98, 103, 126
        $surface1 =      81, 86, 108
        $surface0 =      65, 69, 89
        $base =          48, 52, 70
        $mantle =        41, 44, 60
        $crust =         35, 38, 52

	}

	else if (theme=="Macchiato") {

		$type = dark

        $rosewater =     244, 219, 214
        $flamingo =      240, 198, 198
        $pink =          245, 189, 230
        $mauve =         198, 160, 246
        $red =           237, 135, 150
        $maroon =        238, 153, 160
        $peach =         245, 169, 127
        $yellow =        238, 212, 159
        $green =         166, 218, 149
        $teal =          139, 213, 202
        $blue =          138, 173, 244
        $sapphire =      125, 196, 228
        $sky =           145, 215, 227
        $lavender =      183, 189, 248
        $text =          197, 207, 245
        $subtext1 =      179, 188, 224
        $subtext0 =      161, 170, 203
        $overlay2 =      143, 151, 183
        $overlay1 =      124, 131, 162
        $overlay0 =      108, 114, 141
